import React, { useState, useEffect } from "react";
import "./App.css";
import Table from "./Table.js";

function App() {
  /* Create state:
        - apiData: List containing dictionaries of countries from API.
        - searchQuery: The query parameter that should be added to &search=
        - pageNumber: The page that is requested
  */

  const [apiData, setApiData] = useState([]);
  const [searchQuery, setSearchQuery] = useState(); // Default = No search query
  const [pageNumber, setPageNumber] = useState(1); //Default = Page 1
  const [pageSize, setPageSize] = useState(10)
  const [continents, setContinents] = useState([]); // Default

  useEffect(() => {
    // All parameters are appended to this URL.
    let apiQuery = "https://dhis2-app-course.ifi.uio.no/api?";

    // If searchQuery isn't empty add &search=searchQuery to the API request.
    if (searchQuery) {
      apiQuery = apiQuery + "&search=" + searchQuery;
    }

    // Add what page we are requesting to the API request.
    apiQuery = apiQuery + "&page=" + pageNumber;
    apiQuery = apiQuery + "&pageSize=" + pageSize;
    apiQuery = apiQuery + "&Continent=" + continents.join(",");


    // Query data from API.
    console.log("Querying: " + apiQuery);
    fetch(apiQuery)
      .then((results) => results.json())
      .then((data) => {
        // Then add response to state.
        setApiData(data);
      });
  }, [searchQuery, pageNumber, pageSize, continents]); // Array containing which state changes that should re-reun useEffect()

  const handleSearch = (searchValue) => {
    setPageNumber(1)
    setSearchQuery(searchValue)
  };
  const handleSizeChange = (sizeValue) => {
    setPageNumber(1)
    setPageSize(sizeValue)
  };

  const onPageChange = (pageNum) => {
    if ((pageNumber + pageNum >= 1) && (pageNumber + pageNum <= (apiData?.pager?.pageCount || 1))) {
      setPageNumber(pageNumber + pageNum);
    } 
  }

  const handleContinentChange = (continent) => {
    if (continent.includes(continent)) continents.pop(continent);
    else continent.push(continent);
  }

  console.log(apiData?.pager?.page)

  return (
    <div className="App">
      <h1>Country search</h1>
      <SearchBar onSearch={handleSearch}/>
      <ContinentSelector onContinentChange={handleContinentChange} />
      <Table apiData={apiData} />
      <Pagination onPageChange={onPageChange} totalPage={apiData?.pager?.pageCount} currentPage={pageNumber}/>
      <PageSize handleSizeChange={handleSizeChange} />
    </div>
  );
}

function SearchBar({onSearch}) {
  const [searchValue, setSearchValue] = useState("");

  const handleSearch = () => {onSearch(searchValue)}

  return (
    <div className="search-bar">
      <input
        type="text"
        value={searchValue}
        onChange={(e) => setSearchValue(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

function PageSize({handleSizeChange}) {
  return (
    <div className = "page-size"> 
    <label htmlFor="pageSizeOptions">Results per page:</label>
    <select 
      name="pageSizeOptions" 
      id="pageSizeOptions"
      onChange={(e) => handleSizeChange(e.target.value)}
      >
      <option value="10">10</option>
      <option value="20">20</option>
      <option value="50">50</option>
    </select>
    </div>
  )
}

function Pagination({currentPage, totalPage, onPageChange}) {

  const handleChange = (num) => {
    onPageChange(num);
  }

  return (
    <div className="pagination">
      <label >Page {currentPage} of {totalPage}</label>
      {currentPage > 1 && (
        <button onClick={() => handleChange(-1)}>Previous</button>
      )}
      {currentPage < totalPage && (
        <button onClick={() => handleChange(1)}>Next</button>
      )}
    </div>
  );
}

function ContinentSelector({onContinentChange}) {
  //Checkboxes for each continent
  const handleContinentSelect = (continent) => {
    onContinentChange(continent);
  }

  return (
    <div className="continent-selector">
      <input type="checkbox" value="Africa" id="Africa" onChange={(e) => handleContinentSelect(e.target.value)}/>
      <lable htmlFor="Africa">Africa </lable>
      <input type="checkbox" value="South America" id="South America" onChange={(e) => handleContinentSelect(e.target.value)}/>
      <lable htmlFor="South America">South America</lable>
      <input type="checkbox" value="North America" id="North America" onChange={(e) => handleContinentSelect(e.target.value)}/>
      <lable htmlFor="North America">North America</lable>
      <input type="checkbox" value="Asia" id="Asia" onChange={(e) => handleContinentSelect(e.target.value)}/>
      <lable htmlFor="Asia">Asia</lable>
      <input type="checkbox" value="Europe" id="Europe" onChange={(e) => handleContinentSelect(e.target.value)}/>
      <lable htmlFor="Europe">Europe</lable>
      <input type="checkbox" value="Oceania" id="Oceania" onChange={(e) => handleContinentSelect(e.target.value)}/>
      <lable htmlFor="Oceania">Oceania</lable>
    </div>
  )
}

export default App;
